(function() {
    'use strict';

    console.log('ğŸ›¡ï¸ uBlock Origin JS Ğ·Ğ°Ğ¿ÑƒÑ‰ĞµĞ½');

    // EasyList Ğ¿Ñ€Ğ°Ğ²Ğ¸Ğ»Ğ° (ÑƒĞ¿Ñ€Ğ¾Ñ‰ĞµĞ½Ğ½Ğ°Ñ Ğ²ĞµÑ€ÑĞ¸Ñ)
    const easyListRules = [
        // Ğ”Ğ¾Ğ¼ĞµĞ½Ñ‹
        'googlesyndication.com', 'googleadservices.com', 'doubleclick.net',
        'x47b2v9.com', 's.x47b2v9.com', 'popads.net', 'popcash.net',
        'propellerads.com', 'adnxs.com', 'adsystem.com', 'criteo.com',

        // Ğ¡ĞµĞ»ĞµĞºÑ‚Ğ¾Ñ€Ñ‹
        '.ad', '.ads', '.advertisement', '.banner', '.adsbygoogle',
        '.sponsored', '.promo', '#ad', '#ads',

        // ĞŸĞ°Ñ‚Ñ‚ĞµÑ€Ğ½Ñ‹ URL
        '/ads/', '/banner/', '/popup/', '/d.php'
