    // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ĞºĞ° Ğ¿Ñ€Ğ°Ğ²Ğ¸Ğ» ĞºĞ°Ğº Ğ² uBlock Origin
    function matchesFilter(url, element) {
        if (!url) return false;

        // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼ Ğ´Ğ¾Ğ¼ĞµĞ½Ñ‹
        for (const rule of easyListRules) {
            if (rule.includes('.com') || rule.includes('.net') || rule.includes('.org')) {
                if (url.includes(rule)) return true;
            }
        }

        // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼ Ğ¿Ğ°Ñ‚Ñ‚ĞµÑ€Ğ½Ñ‹ URL
        for (const rule of easyListRules) {
            if (rule.startsWith('/') && rule.endsWith('/')) {
                if (url.includes(rule.slice(1, -1))) return true;
            }
        }

        // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼ ÑĞµĞ»ĞµĞºÑ‚Ğ¾Ñ€Ñ‹ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚Ğ¾Ğ²
        if (element) {
            for (const rule of easyListRules) {
                if (rule.startsWith('.') || rule.startsWith('#')) {
                    if (element.matches && element.matches(rule)) return true;
                    if (element.closest && element.closest(rule)) return true;
                }
            }
        }

        return false;
    }

    // Ğ‘Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²ĞºĞ° ÑĞµÑ‚ĞµĞ²Ñ‹Ñ… Ğ·Ğ°Ğ¿Ñ€Ğ¾ÑĞ¾Ğ² (ĞºĞ°Ğº Ğ² uBlock Origin)
    function blockNetworkRequests() {
        // Fetch API
        const originalFetch = window.fetch;
        window.fetch = function(resource, options) {
            const url = typeof resource === 'string' ? resource : resource.url;

            // Ğ Ğ°Ğ·Ñ€ĞµÑˆĞ°ĞµĞ¼ Ğ·Ğ°Ğ¿Ñ€Ğ¾ÑÑ‹ Ğº Ñ‚Ğ¾Ğ¼Ñƒ Ğ¶Ğµ Ğ´Ğ¾Ğ¼ĞµĞ½Ñƒ
            if (url.startsWith('/') || url.includes(window.location.hostname)) {
                return originalFetch.apply(this, arguments);
            }

            if (matchesFilter(url)) {
                console.log('ğŸ›¡ï¸ uBlock: FETCH Ğ·Ğ°Ğ±Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²Ğ°Ğ½', url);
                return Promise.reject(new Error('uBlock: blocked'));
            }

            return originalFetch.apply(this, arguments);
        };

        // XMLHttpRequest
        const originalOpen = XMLHttpRequest.prototype.open;
        XMLHttpRequest.prototype.open = function(method, url) {
            // Ğ Ğ°Ğ·Ñ€ĞµÑˆĞ°ĞµĞ¼ Ğ·Ğ°Ğ¿Ñ€Ğ¾ÑÑ‹ Ğº Ñ‚Ğ¾Ğ¼Ñƒ Ğ¶Ğµ Ğ´Ğ¾Ğ¼ĞµĞ½Ñƒ
            if (url && (url.startsWith('/') || url.includes(window.location.hostname))) {
                return originalOpen.apply(this, arguments);
            }

            if (matchesFilter(url)) {
                console.log('ğŸ›¡ï¸ uBlock: XHR Ğ·Ğ°Ğ±Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²Ğ°Ğ½', url);
                return;
            }

            return originalOpen.apply(this, arguments);
        };
    }

    // Ğ‘Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²ĞºĞ° ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚Ğ¾Ğ² DOM (ĞºĞ°Ğº Ğ² uBlock Origin)
    function blockDOMElements() {
        // Ğ£Ğ´Ğ°Ğ»ÑĞµĞ¼ ÑÑƒÑ‰ĞµÑÑ‚Ğ²ÑƒÑÑ‰Ğ¸Ğµ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚Ñ‹
        easyListRules.forEach(rule => {
            if (rule.startsWith('.') || rule.startsWith('#')) {
                document.querySelectorAll(rule).forEach(el => {
                    console.log('ğŸ›¡ï¸ uBlock: Ğ­Ğ»ĞµĞ¼ĞµĞ½Ñ‚ ÑƒĞ´Ğ°Ğ»ĞµĞ½', rule);
                    el.remove();
                });
            }
        });

        // Ğ¡Ğ¿ĞµÑ†Ğ¸Ğ°Ğ»ÑŒĞ½Ğ°Ñ Ğ¾Ğ±Ñ€Ğ°Ğ±Ğ¾Ñ‚ĞºĞ° Ğ´Ğ»Ñ Ğ²Ğ°ÑˆĞµĞ³Ğ¾ ÑĞ»ÑƒÑ‡Ğ°Ñ
        document.querySelectorAll('div.item.thumb').forEach(item => {
            const header = item.querySelector('header');
            if (header && header.textContent.trim() === 'AD') {
                console.log('ğŸ›¡ï¸ uBlock: Ğ ĞµĞºĞ»Ğ°Ğ¼Ğ° Ñ Ğ·Ğ°Ğ³Ğ¾Ğ»Ğ¾Ğ²ĞºĞ¾Ğ¼ AD ÑƒĞ´Ğ°Ğ»ĞµĞ½Ğ°');
                item.remove();
                return;
            }

            const link = item.querySelector('a[href*="x47b2v9.com"], a[title="Advertisement"]');
            if (link) {
                console.log('ğŸ›¡ï¸ uBlock: Ğ ĞµĞºĞ»Ğ°Ğ¼Ğ½Ñ‹Ğ¹ Ğ±Ğ»Ğ¾Ğº ÑƒĞ´Ğ°Ğ»ĞµĞ½');
                item.remove();
            }
        });

        // Ğ£Ğ´Ğ°Ğ»ÑĞµĞ¼ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚Ñ‹ Ñ Ñ€ĞµĞºĞ»Ğ°Ğ¼Ğ½Ñ‹Ğ¼Ğ¸ ÑÑÑ‹Ğ»ĞºĞ°Ğ¼Ğ¸
        document.querySelectorAll('*[src], *[href]').forEach(el => {
            const url = el.src || el.href;
            if (matchesFilter(url, el)) {
                const parent = el.closest('.item') || el.closest('div') || el;
                console.log('ğŸ›¡ï¸ uBlock: Ğ­Ğ»ĞµĞ¼ĞµĞ½Ñ‚ Ñ Ñ€ĞµĞºĞ»Ğ°Ğ¼Ğ½Ğ¾Ğ¹ ÑÑÑ‹Ğ»ĞºĞ¾Ğ¹ ÑƒĞ´Ğ°Ğ»ĞµĞ½');
                parent.remove();
            }
        });
    }

    // Ğ‘Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²ĞºĞ° Ğ²ÑĞ¿Ğ»Ñ‹Ğ²Ğ°ÑÑ‰Ğ¸Ñ… Ğ¾ĞºĞ¾Ğ½ (ĞºĞ°Ğº Ğ² uBlock Origin)
    function blockPopups() {
        const originalWindowOpen = window.open;
        window.open = function(url, target, features) {
            // Ğ Ğ°Ğ·Ñ€ĞµÑˆĞ°ĞµĞ¼ Ğ¾ĞºĞ½Ğ° Ñ Ñ‚Ğ¾Ğ³Ğ¾ Ğ¶Ğµ Ğ´Ğ¾Ğ¼ĞµĞ½Ğ° (Ğ½ÑƒĞ¶Ğ½Ñ‹Ğµ Ğ´Ğ»Ñ ÑĞ°Ğ¹Ñ‚Ğ°)
            if (!url || url.startsWith('/') || url.includes(window.location.hostname)) {
                console.log('ğŸ›¡ï¸ uBlock: Ğ Ğ°Ğ·Ñ€ĞµÑˆĞµĞ½Ğ¾ Ğ¾ĞºĞ½Ğ¾ Ñ Ñ‚Ğ¾Ğ³Ğ¾ Ğ¶Ğµ Ğ´Ğ¾Ğ¼ĞµĞ½Ğ°');
                return originalWindowOpen.call(this, url, target, features);
            }

            if (matchesFilter(url)) {
                console.log('ğŸ›¡ï¸ uBlock: Ğ’ÑĞ¿Ğ»Ñ‹Ğ²Ğ°ÑÑ‰ĞµĞµ Ğ¾ĞºĞ½Ğ¾ Ğ·Ğ°Ğ±Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²Ğ°Ğ½Ğ¾', url);
                return null;
            }

            return originalWindowOpen.call(this, url, target, features);
        };
    }

    // ĞĞ°Ğ±Ğ»ÑĞ´Ğ°Ñ‚ĞµĞ»ÑŒ Ğ·Ğ° Ğ¸Ğ·Ğ¼ĞµĞ½ĞµĞ½Ğ¸ÑĞ¼Ğ¸ DOM (ĞºĞ°Ğº Ğ² uBlock Origin)
    function observeDOM() {
        const observer = new MutationObserver(mutations => {
            mutations.forEach(mutation => {
                mutation.addedNodes.forEach(node => {
                    if (node.nodeType === 1) { // Element node
                        // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼ Ğ½Ğ¾Ğ²Ñ‹Ğ¹ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚
                        if (matchesFilter(node.src || node.href, node)) {
                            console.log('ğŸ›¡ï¸ uBlock: ĞĞ¾Ğ²Ñ‹Ğ¹ Ñ€ĞµĞºĞ»Ğ°Ğ¼Ğ½Ñ‹Ğ¹ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚ Ğ·Ğ°Ğ±Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²Ğ°Ğ½');
                            node.remove();
                            return;
                        }

                        // ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµĞ¼ Ğ²Ğ»Ğ¾Ğ¶ĞµĞ½Ğ½Ñ‹Ğµ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚Ñ‹
                        if (node.querySelectorAll) {
                            easyListRules.forEach(rule => {
                                if (rule.startsWith('.') || rule.startsWith('#')) {
                                    node.querySelectorAll(rule).forEach(el => {
                                        console.log('ğŸ›¡ï¸ uBlock: Ğ’Ğ»Ğ¾Ğ¶ĞµĞ½Ğ½Ñ‹Ğ¹ Ñ€ĞµĞºĞ»Ğ°Ğ¼Ğ½Ñ‹Ğ¹ ÑĞ»ĞµĞ¼ĞµĞ½Ñ‚ ÑƒĞ´Ğ°Ğ»ĞµĞ½');
                                        el.remove();
                                    });
                                }
                            });
                        }
                    }
                });
            });

            // ĞŸĞµÑ€Ğ¸Ğ¾Ğ´Ğ¸Ñ‡ĞµÑĞºĞ°Ñ Ğ¾Ñ‡Ğ¸ÑÑ‚ĞºĞ° Ğ¿Ğ¾ÑĞ»Ğµ Ğ¸Ğ·Ğ¼ĞµĞ½ĞµĞ½Ğ¸Ğ¹
            setTimeout(blockDOMElements, 100);
        });

        observer.observe(document.documentElement, {
            childList: true,
            subtree: true
        });
    }

    // Ğ’Ğ°Ñˆ ÑÑƒÑ‰ĞµÑÑ‚Ğ²ÑƒÑÑ‰Ğ¸Ğ¹ ĞºĞ¾Ğ´
    const hookClick = (e) => {
        const origin = e.target.closest('a');
        const isBaseTargetBlank = document.querySelector('head base[target="_blank"]');

        // Ğ‘Ğ»Ğ¾ĞºĞ¸Ñ€ÑƒĞµĞ¼ Ñ€ĞµĞºĞ»Ğ°Ğ¼Ğ½Ñ‹Ğµ ĞºĞ»Ğ¸ĞºĞ¸
        if (origin && origin.href && matchesFilter(origin.href, origin)) {
            e.preventDefault();
            e.stopPropagation();
            console.log('ğŸ›¡ï¸ uBlock: ĞšĞ»Ğ¸Ğº Ğ¿Ğ¾ Ñ€ĞµĞºĞ»Ğ°Ğ¼Ğµ Ğ·Ğ°Ğ±Ğ»Ğ¾ĞºĞ¸Ñ€Ğ¾Ğ²Ğ°Ğ½', origin.href);
            return false;
        }

        console.log('origin', origin, isBaseTargetBlank);
        if ((origin && origin.href && origin.target === '_blank') ||
            (origin && origin.href && isBaseTargetBlank)) {
            e.preventDefault();
            console.log('handle origin', origin);
            location.href = origin.href;
        } else {
            console.log('not handle origin', origin);
        }
    };

    // Ğ˜Ğ½Ğ¸Ñ†Ğ¸Ğ°Ğ»Ğ¸Ğ·Ğ°Ñ†Ğ¸Ñ uBlock Origin JS
    function initUBlockJS() {
        console.log('ğŸ›¡ï¸ Ğ˜Ğ½Ğ¸Ñ†Ğ¸Ğ°Ğ»Ğ¸Ğ·Ğ°Ñ†Ğ¸Ñ uBlock Origin JS...');

        // Ğ—Ğ°Ğ¿ÑƒÑĞºĞ°ĞµĞ¼ Ğ²ÑĞµ Ğ¼Ğ¾Ğ´ÑƒĞ»Ğ¸
        blockNetworkRequests();
        blockDOMElements();
        blockPopups();
        observeDOM();

        // Ğ£ÑÑ‚Ğ°Ğ½Ğ°Ğ²Ğ»Ğ¸Ğ²Ğ°ĞµĞ¼ Ğ¾Ğ±Ñ€Ğ°Ğ±Ğ¾Ñ‚Ñ‡Ğ¸Ğº ĞºĞ»Ğ¸ĞºĞ¾Ğ²
        document.addEventListener('click', hookClick, { capture: true });

        // ĞŸĞµÑ€Ğ¸Ğ¾Ğ´Ğ¸Ñ‡ĞµÑĞºĞ°Ñ Ğ¾Ñ‡Ğ¸ÑÑ‚ĞºĞ° (ĞºĞ°Ğº Ğ² uBlock Origin)
        setInterval(blockDOMElements, 2000);

        // Ğ”Ğ¾Ğ¿Ğ¾Ğ»Ğ½Ğ¸Ñ‚ĞµĞ»ÑŒĞ½Ğ°Ñ Ğ¾Ñ‡Ğ¸ÑÑ‚ĞºĞ° Ğ´Ğ»Ñ x47b2v9.com
        setInterval(() => {
            document.querySelectorAll('*[href*="x47b2v9.com"], *[src*="x47b2v9.com"]').forEach(el => {
                const parent = el.closest('.item') || el.closest('div') || el;
                parent.remove();
            });
        }, 1000);

        console.log('ğŸ›¡ï¸ uBlock Origin JS Ğ°ĞºÑ‚Ğ¸Ğ²Ğ¸Ñ€Ğ¾Ğ²Ğ°Ğ½!');
        console.log('ğŸ›¡ï¸ Ğ—Ğ°Ğ³Ñ€ÑƒĞ¶ĞµĞ½Ğ¾ Ğ¿Ñ€Ğ°Ğ²Ğ¸Ğ»:', easyListRules.length);
    }

    // Ğ—Ğ°Ğ¿ÑƒÑĞº
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', initUBlockJS);
    } else {
        initUBlockJS();
    }

})();
